import { configureStore } from '@reduxjs/toolkit';


const initialState = {
    carritoItems: []
};

function carritoReducer(state = initialState, action) {
    switch (action.type) {
        case 'ADD_TO_CART':
            console.log("Añadiendo al carrito:", action.payload);
            return {
                ...state,
                carritoItems: [...state.carritoItems, action.payload]
            };

        case 'REMOVE_FROM_CART':
            console.log("Removiendo del carrito:", action.payload);
            return {
                ...state,
                carritoItems: state.carritoItems.filter(item => item.id !== action.payload)
            };


        default:
            return state;
    }
}


const store = configureStore ({
    reducer: {
        carrito: carritoReducer
    }
});

export default store;
